// router/index.js
import Vue from 'vue';
import VueRouter from 'vue-router';
import store from '@/store/app';

Vue.use(VueRouter);

const routes = [
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/LoginView.vue'),
  },
  {
    path: '/dashboard',
    name: 'Dashboard',
    component: () => import('@/views/DashboardView.vue'),
    meta: { requiresAuth: true, 
            requiredRole: 'admin' },
  },
  {
    path: '/notifications',
    name: 'Notifications',
    component: () => import('@/views/NotificationsView.vue'),
    meta: { requiresAuth: true },
  },
  // Fallback route redirects any unmatched routes to '/login'
  {
    path: '/:pathMatch(.*)*',
    redirect: '/login',
  },
];

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes,
});

// Navigation guard to check authentication
// Navigation guard to check authentication
router.beforeEach((to, from, next) => {
  const isAuthenticated = store.state.isAuthenticated;

  // If the route requires authentication and the user isn't authenticated
  if (to.matched.some((record) => record.meta.requiresAuth)) {
    if (!isAuthenticated) {
      // User is not authenticated, redirect to login
      next('/login');
    } else {
      // User is authenticated, check if they have the required role (if needed)
      const requiredRole = to.meta.requiredRole;
      if (requiredRole && store.state.role !== requiredRole) {
        // User does not have the required role, you can handle this as needed
        // For example, you could redirect them to an error page
        next('/error');
      } else {
        // User is authenticated and has the required role, allow navigation
        next();
      }
    }
  } else {
    // Route does not require authentication, allow navigation
    next();
  }
});

export default router;
